// import Login from './Login';
import Home from './Home';
import background from './background.jpg';
import React, { useState } from 'react';
import { useAuth0 } from "@auth0/auth0-react";


const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // Store the username and password in localStorage
    localStorage.setItem('username', username);
    localStorage.setItem('password', password);
    // Redirect to the home page or perform any other action you need
    window.location = '/Home';
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" placeholder="Username" onChange={(e) => setUsername(e.target.value)} />
      <input type="password" placeholder="Password" onChange={(e) => setPassword(e.target.value)} />
      <button type="submit">Login</button>
    </form>
  );
};


function Bachg(){
  const { loginWithRedirect } = useAuth0();

    return(
        <div className="bg">
        <img src={background} className="pic" alt="logo" />
        <div className="container">
          <h1>Welcome to the Food Ordering ChatBot</h1>
          <center> <button onClick={(Login) => loginWithRedirect(Home)}>Log In</button>
</center>
        </div>
      </div>
    )
}

export default Bachg;
