import React, { useState } from 'react';

const ChatBot = () => {
  const [message, setMessage] = useState('');
  const [chatHistory, setChatHistory] = useState([]);

  const predefinedAnswers = {
    'Order meal': 'Your meal has been ordered',
    'Cancel meal': 'Your meal has been cancelled',
    'Update meal': 'Your meal has been updated',
  };

  const handleSendMessage = (e) => {
    e.preventDefault();
    const botAnswer = predefinedAnswers[message] || 'I did not understand that';
    setChatHistory([...chatHistory, { user: 'User', message }, { user: 'Bot', message: botAnswer }]);
    setMessage('');
  };

  return (
    <div>
      <ul>
        {chatHistory.map((chat, index) => (
          <li key={index}>
            <strong>{chat.user}:</strong> {chat.message}
          </li>
        ))}
      </ul>
      <form onSubmit={handleSendMessage}>
        <input type="text" value={message} onChange={(e) => setMessage(e.target.value)} />
        <button type="submit">Send</button>
      </form>
    </div>
  );
};

export default ChatBot;
