import React, { useState } from "react";
import botResponses from "./botResponses";

const Chat = () => {
  const [userInput, setUserInput] = useState("");
  const [botReply, setBotReply] = useState("");

  const handleUserInput = (e) => {
    setUserInput(e.target.value);
  };

  const handleSendMessage = () => {
    const matchedQuestion = botResponses.find(
      (response) =>
        response.question.toLowerCase() === userInput.trim().toLowerCase()
    );

    setBotReply(matchedQuestion ? matchedQuestion.answer : botResponses[3].answer);
    setUserInput("");
  };

  return (
    <div>
      <div>
        <input
          type="text"
          value={userInput}
          onChange={handleUserInput}
          placeholder="Enter your query..."
        />
        <button onClick={handleSendMessage}>Send</button>
      </div>
      {botReply && <p>Bot: {botReply}</p>}
    </div>
  );
};

export default Chat;
