// Function to display bot response in the chat log
function Chat(response) {
  const chatLog = document.getElementById("chat-log");
  const botMessage = document.createElement("div");
  botMessage.classList.add("bot-message");
  botMessage.textContent = "Bot: " + response;
  chatLog.appendChild(botMessage);
}

// Function to handle user input and trigger bot responses
function handleUserInput() {
  const userInput = document.getElementById("user-input").value;
  const userMessage = document.createElement("div");
  userMessage.classList.add("user-message");
  userMessage.textContent = "You: " + userInput;
  const chatLog = document.getElementById("chat-log");
  chatLog.appendChild(userMessage);

  // Find the corresponding bot response based on the user input
  let botResponse = getBotResponse(userInput);

  // Display the bot's response
  displayBotResponse(botResponse);

  // Clear the user input field
  document.getElementById("user-input").value = "";
}

// Function to get the bot response using switch case
function getBotResponse(userInput) {
  switch (userInput.toLowerCase()) {
    case "order meal":
      return "Sure! You can place your order on our website.";
    case "cancel meal":
      return "We're sorry to hear that you want to cancel your meal. Please contact our support team at support@example.com for assistance.";
    case "update meal":
      return "To update your meal, please log in to your account and go to the 'My Orders' section.";
    default:
      return "I'm sorry, but I don't understand. Please try asking a different question.";
  }
}

// Event listener for the send button
document.getElementById("send-btn").addEventListener("click", handleUserInput);

// Event listener for the "Enter" key to send the message
document.getElementById("user-input").addEventListener("keyup", function(event) {
  if (event.key === "Enter") {
    handleUserInput();
  }
});

export default Chat;
