import React, { useState } from "react";
import botResponses from "./botResponses";

const Chat = () => {
  const [userInput, setUserInput] = useState("");
  const [botReply, setBotReply] = useState("");

  const handleUserInput = (e) => {
    setUserInput(e.target.value);
  };

  const handleSendMessage = () => {
    const matchedQuestion = botResponses.find(
      (response) =>
        response.question.toLowerCase() === userInput.trim().toLowerCase()
    );

    setBotReply(matchedQuestion ? matchedQuestion.answer : botResponses[3].answer);
    setUserInput("");
  };
  const renderErrorMessage = (name) =>
  name === errorMessages.name && (
    <div className="error">{errorMessages.message}</div>
  );

// JSX code for login form
const renderForm = (
  <div className="form">
    <form onSubmit={handleSubmit}>
      <div className="input-container">
        <label>Username </label>
        <input type="text" name="uname" required />
        {renderErrorMessage("uname")}
      </div>
      <div className="input-container">
        <label>Password </label>
        <input type="password" name="pass" required />
        {renderErrorMessage("pass")}
      </div>
      <div className="button-container">
        <input type="submit" />
      </div>
    </form>
  </div>
);


  return (
    <div>
      <div>
        <input
          type="text"
          value={userInput}
          onChange={handleUserInput}
          placeholder="Enter your query..."
        />
        <button onClick={handleSendMessage}>Send</button>
        {isSubmitted ? <div>User is successfully logged in</div> : renderForm}

      </div>
      {botReply && <p>Bot: {botReply}</p>}
    </div>
  );
};

export default Chat;
