import React, { useState } from 'react';

const Chat = () => {
  const [order, setOrder] = useState([]);
  const [inputText, setInputText] = useState('');

  const handleInputTextChange = (event) => {
    setInputText(event.target.value);
  };

  const handleSendMessage = () => {
    processUserMessage(inputText.trim().toLowerCase());
    setInputText('');
  };

  const processUserMessage = (message) => {
    switch (message) {
      case 'order':
        // Perform the order functionality here
        "Sure! Please let us know what meal you'd like to order."
        break;

      case 'update':
        // Perform the update functionality here
        break;

      case 'cancel':
        // Perform the cancel functionality here
        break;

      default:
        // Handle unrecognized commands here
        break;
    }
  };

  return (
    <div>
      <h1>Food Ordering Chat</h1>
      <div>
        <div>
          {order.map((item, index) => (
            <p key={index}>{item}</p>
          ))}
        </div>
        <div>
          <input
            type="text"
            value={inputText}
            onChange={handleInputTextChange}
            placeholder="Type a command..."
          />
          <button onClick={handleSendMessage}>Send</button>
        </div>
      </div>
    </div>
  );
};

export default Chat;
